INSERT INTO `tblsection` (`SectionID`, `SectionName`, `SectionContent`, `ChapterID`, `AddedByAdminID`, `SectionStatus`, `CreatedDateTime`, `UpdatedDateTime`) VALUES
(60, 'HTML Attributes', '<div class=\"w3-col l10 m12\" id=\"main\" style=\"box-sizing: inherit; float: left; width: 1082.66px; transition: margin-left 0.4s; padding: 16px; color: rgb(0, 0, 0); font-family: Verdana, sans-serif; font-size: 15px;\"><h1 style=\"box-sizing: inherit; font-family: &quot;Segoe UI&quot;, Arial, sans-serif; margin-top: 10px;\"><b>HTML&nbsp;<span class=\"color_h1\" style=\"box-sizing: inherit;\">Global Attributes</span></b></h1><hr style=\"height: 0px; overflow: visible; margin-right: 0px; margin-left: 0px;\"><p class=\"intro\" style=\"box-sizing: inherit; font-size: 16px;\">HTML attributes give elements meaning and context.</p><p class=\"intro\" style=\"box-sizing: inherit; font-size: 16px;\">The global attributes below can be used on&nbsp;<b style=\"box-sizing: inherit;\">any</b>&nbsp;HTML element.</p><hr style=\"height: 0px; overflow: visible; margin-right: 0px; margin-left: 0px;\"><h2 style=\"box-sizing: inherit; font-family: &quot;Segoe UI&quot;, Arial, sans-serif; margin-top: 10px; margin-right: 0px; margin-left: 0px;\"><b>HTML Global Attributes</b></h2><table class=\"w3-table-all notranslate\" style=\"box-sizing: inherit; width: 1050.4px; border: 1px solid rgb(204, 204, 204); margin: 20px 0px;\"><tbody style=\"box-sizing: inherit;\"><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><th style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top; width: 262.4px;\">Attribute</th><th style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Description</th></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_accesskey.asp\" style=\"box-sizing: inherit; color: inherit;\">accesskey</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies a shortcut key to activate/focus an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_class.asp\" style=\"box-sizing: inherit; color: inherit;\">class</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies one or more classnames for an element (refers to a class in a style sheet)</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_contenteditable.asp\" style=\"box-sizing: inherit; color: inherit;\">contenteditable</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies whether the content of an element is editable or not</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_contextmenu.asp\" style=\"box-sizing: inherit; color: inherit;\">contextmenu</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies a context menu for an element. The context menu appears when a user right-clicks on the element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_data.asp\" style=\"box-sizing: inherit; color: inherit;\">data-*</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Used to store custom data private to the page or application</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_dir.asp\" style=\"box-sizing: inherit; color: inherit;\">dir</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies the text direction for the content in an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_draggable.asp\" style=\"box-sizing: inherit; color: inherit;\">draggable</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies whether an element is draggable or not</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_dropzone.asp\" style=\"box-sizing: inherit; color: inherit;\">dropzone</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies whether the dragged data is copied, moved, or linked, when dropped</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_hidden.asp\" style=\"box-sizing: inherit; color: inherit;\">hidden</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies that an element is not yet, or is no longer, relevant</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_id.asp\" style=\"box-sizing: inherit; color: inherit;\">id</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies a unique id for an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_lang.asp\" style=\"box-sizing: inherit; color: inherit;\">lang</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies the language of the element\'s content</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_spellcheck.asp\" style=\"box-sizing: inherit; color: inherit;\">spellcheck</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies whether the element is to have its spelling and grammar checked or not</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_style.asp\" style=\"box-sizing: inherit; color: inherit;\">style</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies an inline CSS style for an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_tabindex.asp\" style=\"box-sizing: inherit; color: inherit;\">tabindex</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies the tabbing order of an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221); background-color: rgb(241, 241, 241);\"><td style=\"box-sizing: inherit; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_title.asp\" style=\"box-sizing: inherit; color: inherit;\">title</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies extra information about an element</td></tr><tr style=\"box-sizing: inherit; border-bottom: 1px solid rgb(221, 221, 221);\"><td class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: right center; padding: 8px 8px 8px 16px; vertical-align: top;\"><a href=\"https://www.w3schools.com/tags/att_global_translate.asp\" style=\"box-sizing: inherit; color: inherit;\">translate</a></td><td style=\"box-sizing: inherit; padding: 8px; vertical-align: top;\">Specifies whether the content of an element should be translated or not</td></tr></tbody></table></div>', 7, 1, 0, '2018-03-31 14:28:19', '2018-04-02 20:21:46');
INSERT INTO `tblsection` (`SectionID`, `SectionName`, `SectionContent`, `ChapterID`, `AddedByAdminID`, `SectionStatus`, `CreatedDateTime`, `UpdatedDateTime`) VALUES
(61, 'HTML Events', '<div class=\"w3-col l10 m12\" id=\"main\" style=\"box-sizing: inherit; float: left; width: 1082.66px; transition: margin-left 0.4s; padding: 16px; color: rgb(0, 0, 0); font-family: Verdana, sans-serif; font-size: 15px;\"><h1 style=\"box-sizing: inherit; font-family: &quot;Segoe UI&quot;, Arial, sans-serif; margin-top: 10px;\"><b>HTML&nbsp;<span class=\"color_h1\" style=\"box-sizing: inherit;\">Event Attributes</span></b></h1><hr style=\"height: 0px; overflow: visible; margin-right: 0px; margin-left: 0px;\"><h2 style=\"box-sizing: inherit; font-family: &quot;Segoe UI&quot;, Arial, sans-serif; margin-top: 10px; margin-right: 0px; margin-left: 0px;\"><b>Global Event Attributes</b></h2><p style=\"box-sizing: inherit;\">HTML 4 added the ability to let events trigger actions in a browser, like starting a JavaScript when a user clicks on an element.</p><p style=\"box-sizing: inherit;\">To learn more about programming events, please visit our&nbsp;<a href=\"https://www.w3schools.com/js/default.asp\" style=\"box-sizing: inherit; color: inherit;\">JavaScript tutorial</a>.</p><p style=\"box-sizing: inherit;\">Below are the global event attributes that can be added to HTML elements to define event actions.</p><p class=\"html5badge\" style=\"box-sizing: inherit; background-image: url(&quot;/images/html5_badge20.png&quot;); background-repeat: no-repeat; background-position: left center; padding-left: 20px; height: 18px;\">= New event attributes in HTML5.</p><hr style=\"height: 0px; overflow: visible; margin-right: 0px; margin-left: 0px;\"><h2 style=\"box-sizing: inherit; font-family: &quot;Segoe UI&quot;, Arial, sans-serif; margin-top: 10px; margin-right: 0px; margin-left: 0px;\"><b>Window Event Attributes</b></h2><p style=\"box-sizing: inherit;\">Events triggered for the window object (applies to the &lt;body&gt; tag):</p><table class=\"w3-table-all notranslate\" style=\"box-sizing: inherit; width: 1050.4px; border: 1px solid rgb(204, 204, 204); margin: 20px